package com.tnsif.dayone;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a=13;
		int i=a;
		System.out.println("Byte to Int"+i);
		float b=20.20f;
		double c=b;
		System.out.println("Float to Double"+c);
		
		

	}

}
